SELECT A.G3E_ID           G3E_ID,
       A.G3E_FNO          G3E_FNO,
       A.G3E_CNO          G3E_CNO,
       A.G3E_FID          G3E_FID,
       A.G3E_CID          G3E_CID,
       A.G3E_GEOMETRY     G3E_GEOMETRY,
       A.G3E_ALIGNMENT    G3E_ALIGNMENT,
       A.SEQ              SEQ,
       B.COUNT_ANNOTATION COUNT_ANNOTATION,
       C.JOB_ID           JOB_ID,
       C.JOB_STATE        JOB_STATE,
       C.FEATURE_STATE    FEATURE_STATE,
       C.SAP_WRK_ID       SAP_WRK_ID
